import classes from "./alert.module.css";

export const alert = (msg) => {
    const lower = String(msg).toLowerCase().replace('_', ' ');
    const msgText = lower.charAt(0).toUpperCase() + lower.slice(1);
    errorMessage = (
        <p key={msgText} className = {classes.alert}variant="danger">{msgText}</p>
    );
    return msg;
}
