import React from 'react';
import classes from './Ingredients.module.css';

const ingredients = (props) => {           
    const  ingStyle={transform: "rotate("+360*props.indx/props.numOfIngs+"deg) translate("+5+"em) rotate(-"+360*props.indx/props.numOfIngs +"deg)"};
    return (
    <div className={classes.Ingredients} style={ingStyle}>
        <img src={require(`../../../../assets/images/pokeIngredients/${props.imgName}`).default} alt=""></img>
    </div>);
}

export default ingredients;
