import React from 'react';
import classes from './BuildControls.module.css';
import BuildControl from './BuildControl/BuildControl';
import Auxilliary from '../../../hoc/Auxilliary';

const controls = {
    base: {
        value: '',
        ingredients: [
            { label: 'Quinoa', type: 'quinoa' },
            { label: 'White Rice', type: 'whiterice' },
            { label: 'Brown Rice', type: 'brownrice' },],
    },
    proteins: [
        { label: 'Prawn', type: 'prawn' },
        { label: 'Egg', type: 'egg' },],
    toppings: [
        { label: 'Lettuce', type: 'lettuce' },],

};


const selectionChangedHandler = (event, props) => {
    controls.base.value = event.target.value;
    let resetIngNames = []
    controls.base.ingredients.forEach(ing => {
        if (ing.type !== controls.base.value) {
            resetIngNames.push(ing.type);
        }
    });
    props.ingredientsReset(resetIngNames);
    props.ingredientAdded(controls.base.value);
}


const buildControls = (props) => {
    console.log(props.animationCSS);
let animationClass = [classes.Controls];
if (props.animationCSS === "up") {
    animationClass = [classes.Controls, classes.goUp];
    console.log(animationClass);
}
if (props.animationCSS === "down"){
    animationClass= [classes.Controls, classes.goDown];
    console.log(animationClass);
}
    let transformedControls = [];
    transformedControls.push(<p className={classes.title}>Choose the {props.category}</p>)
    if (props.category === 'base') {
        controls['base'].ingredients.map(base => (transformedControls.push(
            <div className={classes.radioGroup}>
                <label className={classes.radioLabel} >
                <input type="radio" id={base.type} name="base" value={base.type} onChange={(event) => selectionChangedHandler(event, props)}></input>
                <span className={classes.innerLabel}>{base.label}</span>
                </label>
            </div>
        )));
        // transformedControls.push(controls['base'].ingredients.map(base => (
        //     <Auxilliary>
        //         <input className={classes.radio} type="radio" id={base.type} name="base" value={base.type} onChange={(event) => selectionChangedHandler(event, props)}></input>
        //         <label className={[classes.radio,classes.label].join(' ')} for={base.label}>{base.label}</label>
        //     </Auxilliary>)));
    }




    // if (props.category === 'base') {
    //     transformedControls.push(
    //         <select
    //             value={controls.base.value}
    //             onChange={(event) => selectionChangedHandler(event, props)}>
    //             {controls['base'].ingredients.map(base => (
    //                 <option key={base.type}
    //                     value={base.type}>
    //                     {base.label}
    //                 </option>)
    //             )}
    //         </select>);
    // }
    else {
        controls[props.category].forEach(ctrl => {
            transformedControls.push(
                <BuildControl
                    key={ctrl.label}
                    label={ctrl.label}
                    added={() => props.ingredientAdded(ctrl.type)}
                    removed={() => props.ingredientRemoved(ctrl.type)}
                    disable={props.disable[ctrl.type]}
                    numIngs={props.ings[ctrl.type]} 
                    animationCSS={props.animationCSS}
                    animationReset={props.animationReset}
                    />
            );
        });
    }



    // Object.keys(controls)
    //     .forEach(category => {
    //         transformedControls.push(<p>Choose the {category}</p>)
    //         // console.log(props);
    //         if (category === 'base') {
    //             transformedControls.push(
    //                 <select
    //                     value={category.value}
    //                     onChange={(event) => selectionChangedHandler(event, props)}>
    //                     {controls[category].ingredients.map(base => (
    //                         <option key={base.type}
    //                             value={base.type}>
    //                             {base.label}
    //                         </option>)
    //                     )}
    //                 </select>);
    //         }
    //         else {
    //             controls[category].forEach(ctrl => {
    //                 transformedControls.push(
    //                     <BuildControl
    //                         key={ctrl.label}
    //                         label={ctrl.label}
    //                         added={() => props.ingredientAdded(ctrl.type)}
    //                         removed={() => props.ingredientRemoved(ctrl.type)}
    //                         disable={props.disable[ctrl.type]}
    //                         numIngs={props.ings[ctrl.type]} />
    //                 );
    //             });
    //         }

    //     });
    // let leftArrowClasses = [classes.arrow, classes.left];
    // let leftArrowDisabledClasses = [classes.arrow, classes.left, classes.disabled];
    // let rightArrowClasses = [classes.arrow, classes.right];
    // let rightArrowDisabledClasses = [classes.arrow, classes.right, classes.disabled];

    return (
        <div>
            <div className={animationClass.join(' ')} onAnimationEnd={props.animationReset}>
            {transformedControls}
            </div>            
            <p className={classes.totalPrice}>Total Price: {props.totalPrice.toFixed(2)}</p>
            <div>
                <button
                    className={classes.fillButton}
                    disabled={!props.purchasable}
                    onClick={props.ordered}>
                    {props.isAuth ? "Order Now" : "Sign In to Order"}
                </button>
            </div>
        </div>
        // <div className={classes.BuildControls}>
        //     <fieldset>
        //         <p>Total Price: {props.totalPrice.toFixed(2)}</p>
        //         {transformedControls}

        //         {/* <input type="button" name="next" className={classes.actionButton} value="Next" /> */}
        //         <div>
        //             <button
        //                 className={classes.actionButton}
        //                 disabled={!props.purchasable}
        //                 onClick={props.ordered}>
        //                 {props.isAuth ? "Order Now" : "Sign In to Order"}
        //             </button>
        //         </div>
        //         {props.goUpDisable ? <a href="#" className={leftArrowDisabledClasses.join(' ')} onClick={props.goUp}></a> :
        //             <a href="#" className={leftArrowClasses.join(' ')} onClick={props.goUp}></a>}

        //         {props.goDownDisable ? <a href="#" className={rightArrowDisabledClasses.join(' ')} onClick={props.goDown}></a> :
        //             <a href="#" className={rightArrowClasses.join(' ')} onClick={props.goDown}></a>}
        //     </fieldset>
        // </div>
    )

}
{/* <div className={classes.BuildControls}>
            <p>Total Price: {props.totalPrice.toFixed(2)}</p> */}




