import classes from './ContactData.module.css';
import React, { Component } from 'react';
import Button from '../../../components/UI/Button/Button';
import axios from '../../../axios-orders';

class ContactData extends Component {
    state = {
        name: '',
        phone: '',
        address: {
            street: '',
            postalCode: ''
        },
        loading: false
    }

    orderHandler = (event) => {
        event.preventDefault();

        this.setState({ loading: true });
        //console.log('loading: ', this.state.loading);
        const order = {
            ingredients: this.props.ingredients,
            price: this.props.price,
            customer: {
                name: 'Test',
                address: '123 test street',
                phone: '0411000000',
            },
            deliveryMethods: 'leave at door'
        };
        axios.post('/orders.json', order)
            .then(this.setState({ loading: false }))
            .catch(this.setState({ loading: false }));
    }

    render() {
        return (
            <div>
                <h4>Please Enter your Contact Info</h4>
                <form>
                    <input className={classes.Input} type="text" name="name" placeholder="Your Name" />
                    <input className={classes.Input} type="text" name="phone" placeholder="Your Phone" />
                    <input className={classes.Input} type="text" name="street" placeholder="Street" />
                    <input className={classes.Input} type="text" name="postcode" placeholder="Post Code" />
                    <Button btnType="Success"
                        clicked={this.orderHandler}>
                        Order</Button>
                </form>
            </div>
        );
    }
}

export default ContactData;
