import React from 'react';
import classes from './Ingredients.module.css';

const ingredients = (props) => {           
    let ingStyle = {};
    let ingClasses = [classes.Ingredients];
    if(props.type !== "base"){
      ingStyle={transform: "rotate("+360*props.indx/props.numOfIngs+"deg) translate("+50+"%) rotate(-"+360*props.indx/props.numOfIngs +"deg)"};
    }
    else{
        ingClasses=[classes.Ingredients,classes.Base];
    }
    return (
    <li className={classes.Ingredients} style={ingStyle}>
        <img src={require(`../../../../assets/images/pokeIngredients/${props.imgName}`).default} alt=""></img>
    </li>);
}

export default ingredients;
