        if (event.target.value === "3") {
            this.setState({ step: <h1>Step 3</h1> });
        }

    }

    render() {
        return (

            <div className={classes.pagination}>
                <button className={classes.button} value="1" onClick={event => { this.loadSteps(event) }}>1</button>
                <button className={classes.button} value="2" onClick={event => { this.loadSteps(event) }}>2</button>
                <button className={classes.button} value="3" onClick={event => { this.loadSteps(event) }}>3</button>
                {this.state.step}
                <div className={classes.downArrow}>
                    <div className={classes.bounce}>
                        <img width="40" height="40" alt="" src="data:image/svg+xml;base64," /></div>
                </div>
            </div>

        );
    };

}


export default TestUI;
