import React, { Component } from 'react';
import classes from './Carousel.module.css';
import BuildControls from '../../components/Poke/BuildControls/BuildControls';

class Carousel extends Component {

    constructor(props) {
        super(props);
        this.button1 = React.createRef();
        this.button2 = React.createRef();
        this.button3 = React.createRef();
      }

    state = {
        index: "1",
        showCheckoutButton: false
    };



    toggleSteps = (idx) =>{
        if (idx === "1") {
            this.setState({ index:"1", showCheckoutButton: false});
            this.button1.current.style.background = 'darkslategray';
            this.button2.current.style.background = 'gray';
            this.button3.current.style.background = 'gray';            
            this.button1.current.style.opacity = 0.5;
            this.button2.current.style.opacity = 0.2;
            this.button3.current.style.opacity = 0.2;
        }
        if (idx === "2") {
            this.setState({index:"2", showCheckoutButton: false});
            this.button1.current.style.background = 'gray';
            this.button2.current.style.background = 'darkslategray';
            this.button3.current.style.background = 'gray';        
            this.button1.current.style.opacity = 0.2;
            this.button2.current.style.opacity = 0.5;
            this.button3.current.style.opacity = 0.2;
        }
        if (idx === "3") {
            this.setState({index:"3", showCheckoutButton: true});
            this.button1.current.style.background = 'gray';
            this.button2.current.style.background = 'gray';
            this.button3.current.style.background = 'darkslategray';        
            this.button1.current.style.opacity = 0.2;
            this.button2.current.style.opacity = 0.2;
            this.button3.current.style.opacity = 0.5;
        }
    }

    loadSteps = (event) => {
        this.toggleSteps(event.target.value);
    }

    stepsGoDown = () =>{
        this.toggleSteps(String(this.state.index % 3+1) );        
    }

