        if (this.state.index === "2") {
                step= <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset = {this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.props.ordered}
                disable={this.props.disable}
                ings = {this.props.ings} 
                category = "proteins"/>;
        }
        if (this.state.index === "3") {
                step=<BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset = {this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.props.ordered}
                disable={this.props.disable}
                ings = {this.props.ings} 
                category = "toppings"/>;
        }
        
        return (

            <div className={classes.progress_inner} >
               
            <div className={classes.progress_inner__step}>
                <label for={classes.step1}>Start order</label>
            </div>
            <div className={classes.progress_inner__step}>
                <label for={classes.step2}>Prepare gift</label>
            </div>
            <div className={classes.progress_inner__step}>
                <label for={classes.step3}>Pack gift</label>
            </div>
            <div className={classes.progress_inner__step}>
                <label for={classes.step4}>Decorate box</label>
            </div>
            <div className={classes.progress_inner__step}>
                <label for={classes.step5}>Send gift</label>
            </div>
            <input checked="checked" id={classes.step1} name="step" type="radio" />
            <input checked="checked" id={classes.step2} name='step' type='radio' />
            <input checked="checked" id={classes.step3} name='step' type='radio' />
            <input checked="checked" id={classes.step4} name='step' type='radio' />
            <input checked="checked" id= {classes.step5}name='step' type='radio' />
            <div className={classes.progress_inner__bar}></div>
            <div className={classes.progress_inner__bar_set}></div>
            <div className={classes.progress_inner__tabs}>
                <div className={classes.tab}>
                    <h1>Start order</h1>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris tortor ipsum, eleifend vitae massa non, dignissim finibus eros. Maecenas non eros tristique nisl maximus sollicitudin.</p>
