import React, { Component } from 'react';
import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/Button';
import classes from './Auth.module.css';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
import Spinner from '../../components/UI/Spinner/Spinner';
import { Redirect } from 'react-router-dom';
import { checkValidity } from '../../shared/utility';
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';
import bgImg from '../../assets/images/pokeIngredients/cinnamon.png'
class Auth extends Component {

    state = {
        controls: {
            signUp: {
                email: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'email',
                        placeholder: 'Email'
                    },
                    value: '',
                    validation: {
                        required: true,
                        isEmail: true
                    },
                    validity: false,
                    touched: false
                },
                password: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'password',
                        placeholder: 'Password'
                    },
                    value: '',
                    validation: {
                        required: true,
                        minLength: 6
                    },
                    validity: false,
                    touched: false
                },
                name: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'name',
                        placeholder: 'Name'
                    },
                    value: '',
                    validation: {
                        required: true,
                        maxLength: 25
                    },
                    validity: false,
                    touched: false
                },
            },
            signIn: {
                email: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'email',
                        placeholder: 'Email'
                    },
                    value: '',
                    validation: {
                        required: true,
                        isEmail: true
                    },
                    validity: false,
                    touched: false
                },
                password: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'password',
                        placeholder: 'Password'
                    },
                    value: '',
                    validation: {
                        required: true,
                        minLength: 6
                    },
                    validity: false,
                    touched: false
                },
            }
        },
        isSignUp: true
    }

    inputChangedHandler = (event, controlType, controlName) => {
        const updatedControls = {
            ...this.state.controls[controlType],
            [controlName]: {
                ...this.state.controls[controlType][controlName],
                value: event.target.value,
                validity: checkValidity(event.target.value, this.state.controls[controlType][controlName].validation),
                touched: true
            }
        };
        this.setState(({ controls }) => ({ controls: { ...controls, [controlType]: updatedControls } }));
    }

    submitHandler = (event, type) => {
        event.preventDefault();
        this.props.onAuth(this.state.controls[type].email.value, this.state.controls[type].password.value, this.state.isSignUp);
    }

    switchAuthModeHandler = () => {
        this.setState(prevState => {
            return {
                isSignUp: !prevState.isSignUp
            }
        });
    }

    signUpErrorMessage = () => { //rearrang the error message from firebase
        let errorMessage = null;
        if (this.props.signUpError) {
            const lower = String(this.props.signUpError.message).toLowerCase().replace('_', ' ');
            const msgText = lower.charAt(0).toUpperCase() + lower.slice(1);
            errorMessage = (
                <Alert key={msgText} variant="danger">{msgText}</Alert>
            );
        } return errorMessage;
    }
    signInErrorMessage = () => { //rearrang the error message from firebase
        let errorMessage = null;

        if (this.props.signInError) {
            const lower = String(this.props.signInError.message).toLowerCase().replace('_', ' ');
            const msgText = lower.charAt(0).toUpperCase() + lower.slice(1);
            errorMessage = (
                <Alert key={msgText} variant="danger">{msgText}</Alert>
            );
        }
        return errorMessage;
    }


    componentDidMount() {
        if (!this.props.buildingPoke && this.props.authRedirectPath !== '/') {
            this.props.onSetAuthRedirectPath();
        }
    }

    arrangeForm = (type) => {
        const formElementsArray = [];
        for (let key in this.state.controls[type]) {
            formElementsArray.push({
                id: key,
                config: this.state.controls[type][key]
            });
        }

        let form = formElementsArray.map(formElement => (
            <Input
                key={formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                valid={formElement.config.validity}
                touched={formElement.config.touched}
                changed={(event) => this.inputChangedHandler(event, type, formElement.id)}
                backgroundImg={bgImg}
                required={formElement.config.required}
                />
        ));
        return form;
    }

    render() {

        let signUpForm = this.arrangeForm('signUp');
        let signInForm = this.arrangeForm('signIn');

        if (this.props.loading) {
            signInForm = <Spinner />
            signUpForm = <Spinner />
        }
        const signUpErrorMessage = this.signUpErrorMessage();
        const signInErrorMessage = this.signInErrorMessage();


        let authRedirect = null;
        if (this.props.isAuthenticated) {
            authRedirect = <Redirect to={this.props.authRedirectPath} />
        }

        return (
            <div className={classes.loginBG}>
                <div className={classes.section}>
                    <h1 className={classes.heading}>
                        <span>Log In</span>
                        <span>Sign Up</span>
                    </h1>
                    <input className={classes.checkbox} type="checkbox" id="reg-log" name="reg-log" onClick={this.switchAuthModeHandler} />
                    <label for="reg-log"></label>
                    <div className={classes.card3dWrap}>
                        <div className={classes.card3dWrapper}>

                            <form onSubmit={event => { this.submitHandler(event, 'signUp') }} className={classes.cardFront}>
                                <div className={classes.centerWrap}>
                                    <h2 >Sign Up</h2>
                                    {signUpForm}
                                    {signUpErrorMessage}
                                    <button className={[classes.box, classes.shadow].join(' ')}>
                                        <div className={classes.circle}></div>
                                        Submit</button>
                                </div>
                            </form>

                            <form onSubmit={event => { this.submitHandler(event, 'signIn') }} className={classes.cardBack}>
                                <div >
                                    <div className={classes.centerWrap}>
                                        <h2 >Log In</h2>
                                        {signInForm}
                                        {signInErrorMessage}
                                        <button className={[classes.box, classes.shadow].join(' ')}>
                                            <div className={classes.circle}></div>
                                        Submit</button>
                                        {/* <p className="mb-0 mt-4 text-center"><a href="#0" className={classes.link}>Forgot your password?</a></p> */}
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
}

const mapStateToProps = state => {
    return {
        loading: state.auth.loading,
        signInError: state.auth.signInError,
        signUpError: state.auth.signUpError,
        isAuthenticated: state.auth.token !== null,
        buildingPoke: state.pokeBuilder.building,
        authRedirectPath: state.auth.authRedirectPath
    }
}



const mapDispatchToProps = dispatch => {
    return {
        onAuth: (email, password, isSignUp) => dispatch(actions.auth(email, password, isSignUp)),
        onSetAuthRedirectPath: () => dispatch(actions.setAuthRedirectPath('/'))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Auth);





