import React, { Component, useState, useEffect } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBStepper, MDBStep, MDBBtn, MDBInput } from "mdbreact";
import classes from './TestUI.module.css';
import Auxilliary from '../../../hoc/Auxilliary';
import { motion, useMotionValue, useSpring } from "framer-motion"

function TestUI() {

  return (<Auxilliary>

    <div className={classes.section}>
      <div className={classes.container}>

              <h6 className="mb-0 pb-3"><span>Log In </span><span>Sign Up</span></h6>
              <input className={classes.checkbox} type="checkbox" id="reg-log" name="reg-log" />
              <label for="reg-log"></label>
              <div className={classes.card3dWrap}>
                <div className={classes.card3dWrapper}>
                  <div className={classes.cardFront}>
                    <div className={classes.centerWrap}>
                      <div className={classes.section}>
                        <h4 className="mb-4 pb-3">Log In</h4>
                        <div className={classes.formGroup}>
                          <input type="email" name="logemail" className={classes.formStyle} placeholder="Your Email" id="logemail" autocomplete="off" />
                          <i className={classes.inputIcon}></i>
                        </div>
                        <div className={classes.formGroup}>
                          <input type="password" name="logpass" className={classes.formStyle} placeholder="Your Password" id="logpass" autocomplete="off" />
                          <i className={classes.inputIcon}></i>
                        </div>
                        <a href="#" className={classes.btn}>submit</a>
                        <p className="mb-0 mt-4 text-center"><a href="#0" className={classes.link}>Forgot your password?</a></p>
                      </div>
                    </div>
                  </div>
                  <div className={classes.cardBack}>
                    <div className={classes.centerWrap}>
                      <div className={classes.section} >
                        <h4 className="mb-4 pb-3">Sign Up</h4>
                        <div className={classes.formGroup}>
                          <input type="text" name="logname" className={classes.formStyle} placeholder="Your Full Name" id="logname" autocomplete="off" />
                          <i className={classes.inputIcon}></i>
                        </div>
                        <div className={classes.formGroup}>
                          <input type="email" name="logemail" className={classes.formStyle} placeholder="Your Email" id="logemail" autocomplete="off" />
                          <i className={classes.inputIcon}></i>
                        </div>
                        <div className={classes.formGroup}>
                          <input type="password" name="logpass" className={classes.formStyle} placeholder="Your Password" id="logpass" autocomplete="off" />
                          <i className={classes.inputIcon}></i>
                        </div>
                        <a href="#" className={classes.btn}>submit</a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

      </div>
    </div>
  </Auxilliary>);
};
