import React from 'react';
import classes from './BuildControls.module.css';
import BuildControl from './BuildControl/BuildControl';
import { checkPropTypes } from 'prop-types';

const controls = {
    base: {
        value: '',
        ingredients: [
            { label: 'Quinoa', type: 'quinoa' },
            { label: 'White Rice', type: 'whiterice' },
            { label: 'Brown Rice', type: 'brownrice' },],
    },
    proteins: [
        { label: 'Prawn', type: 'prawn' },
        { label: 'Egg', type: 'egg' },],
    toppings: [
        { label: 'Lettuce', type: 'lettuce' },],

};


const selectionChangedHandler = (event, props) => {
    controls.base.value = event.target.value;
    let resetIngNames = []
    controls.base.ingredients.forEach(ing => {
        if (ing.type !== controls.base.value) {
            resetIngNames.push(ing.type);
        }
    });
    props.ingredientsReset(resetIngNames);
    props.ingredientAdded(controls.base.value);
}



const buildControls = (props) => {
    let transformedControls = [];
    transformedControls.push(<p>Choose the {props.category}</p>)
    if (props.category === 'base') {
        transformedControls.push(
            <select
                value={controls.base.value}
                onChange={(event) => selectionChangedHandler(event, props)}>
                {controls['base'].ingredients.map(base => (
                    <option key={base.type}
                        value={base.type}>
                        {base.label}
                    </option>)
                )}
            </select>);
    }
    else {
        controls[props.category].forEach(ctrl => {
            transformedControls.push(
                <BuildControl
                    key={ctrl.label}
                    label={ctrl.label}
                    added={() => props.ingredientAdded(ctrl.type)}
                    removed={() => props.ingredientRemoved(ctrl.type)}
                    disable={props.disable[ctrl.type]}
                    numIngs={props.ings[ctrl.type]} />
            );
        });
    }



    // Object.keys(controls)
    //     .forEach(category => {
    //         transformedControls.push(<p>Choose the {category}</p>)
    //         // console.log(props);
    //         if (category === 'base') {
    //             transformedControls.push(
    //                 <select
    //                     value={category.value}
    //                     onChange={(event) => selectionChangedHandler(event, props)}>
    //                     {controls[category].ingredients.map(base => (
    //                         <option key={base.type}
    //                             value={base.type}>
    //                             {base.label}
    //                         </option>)
    //                     )}
    //                 </select>);
    //         }
    //         else {
    //             controls[category].forEach(ctrl => {
    //                 transformedControls.push(
    //                     <BuildControl
    //                         key={ctrl.label}
    //                         label={ctrl.label}
    //                         added={() => props.ingredientAdded(ctrl.type)}
    //                         removed={() => props.ingredientRemoved(ctrl.type)}
    //                         disable={props.disable[ctrl.type]}
    //                         numIngs={props.ings[ctrl.type]} />
    //                 );
    //             });
    //         }

    //     });
    let leftArrowClasses = [classes.arrow, classes.left];
    let leftArrowDisabledClasses = [classes.arrow, classes.left, classes.disabled];
    let rightArrowClasses = [classes.arrow, classes.right];
    let rightArrowDisabledClasses = [classes.arrow, classes.right,classes.disabled];

    return (<div className={classes.BuildControls}>
        <fieldset>
            <p>Total Price: {props.totalPrice.toFixed(2)}</p>
            {transformedControls}

            {/* <input type="button" name="next" className={classes.actionButton} value="Next" /> */}
            <div>
                <button
                    className={classes.actionButton}
                    disabled={!props.purchasable}
                    onClick={props.ordered}>
                    {props.isAuth ? "Order Now" : "Sign In to Order"}
                </button>
            </div>
            {props.goUpDisable ?  <a href="#" className={leftArrowDisabledClasses.join(' ')} onClick={props.goUp}></a>:
            <a href="#" className={leftArrowClasses.join(' ')} onClick={props.goUp}></a>}
           
            {props.goUpDisable ?  <a href="#" className={rightArrowDisabledClasses.join(' ')} onClick={props.goDown}></a>:
            <a href="#" className={rightArrowClasses.join(' ')} onClick={props.goDown}></a>}
           
            
        </fieldset>
    </div>)

}
