import React, { Component } from 'react';
import classes from './Carousel.module.css';
import BuildControls from '../../components/Poke/BuildControls/BuildControls';
import Auxilliary from '../../hoc/Auxilliary';
import { findAllByDisplayValue } from '@testing-library/dom';

const TOTAL_STEPS = "3";

class Carousel extends Component {

    constructor(props) {
        super(props);
        this.button1 = React.createRef();
        this.button2 = React.createRef();
        this.button3 = React.createRef();
    }

    state = {
        index: "1",
        showCheckoutButton: false,
        goUpDisable: true,
        goDownDisable: false
    };



    toggleSteps = (idx) => {
        if (idx === "1") {
            this.setState({ index: "1", showCheckoutButton: false });
            this.button1.current.checked = true;
            this.button2.current.checked = false;
            this.button3.current.checked = false;
        }
        if (idx === "2") {
            this.setState({ index: "2", showCheckoutButton: false });
            this.button1.current.checked = false;
            this.button2.current.checked = true;
            this.button3.current.checked = false;
        }
        if (idx === "3") {
            this.setState({ index: "3", showCheckoutButton: true });
            this.button1.current.checked = false;
            this.button2.current.checked = false;
            this.button3.current.checked = true;
        }
    }

    loadSteps = (event) => {
        this.toggleSteps(event.target.value);
    }

    stepsGoDown = () => {
        if (this.state.index !== TOTAL_STEPS) {
            this.toggleSteps(String(Number(this.state.index) + 1));
        }
    }
    stepsGoUp = () => {
        if (this.state.index !== "1") {
            this.toggleSteps(String(Number(this.state.index) - 1));
        }
    }

    render() {
        let step = null;
        if (this.state.index === "1") {
            this.setState({goUpDisable: true, goDownDisable:false})
            step = <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset={this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.props.ordered}
                disable={this.props.disable}
                ings={this.props.ings}
                goUp={(event => this.stepsGoUp(event))}
                goUpDisable={true}
                goDown={(event => this.stepsGoDown(event))}
                goDownDisable={false}
                category="base" />;
        }
        if (this.state.index === "2") {
            this.setState({goUpDisable: false, goDownDisable:false})

            step = <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset={this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.props.ordered}
                disable={this.props.disable}
                ings={this.props.ings}
                goUp={(event => this.stepsGoUp(event))}
                goUpDisable={false}
                goDown={(event => this.stepsGoDown(event))}
                goDownDisable={false}
                category="proteins" />;
        }
        if (this.state.index === "3") {
            this.setState({goUpDisable: false, goDownDisable:true})

            step = <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset={this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.props.ordered}
                disable={this.props.disable}
                ings={this.props.ings}
                goUp={(event => this.stepsGoUp(event))}
                goUpDisable={false}
                goDown={(event => this.stepsGoDown(event))}
                goDownDisable={true}
                category="toppings" />;
        }
        let leftArrowClasses = [classes.arrow, classes.left];
        let leftArrowDisabledClasses = [classes.arrow, classes.left, classes.disabled];
        let rightArrowClasses = [classes.arrow, classes.right];
        let rightArrowDisabledClasses = [classes.arrow, classes.right, classes.disabled];
        return (
            <Auxilliary>
                <div className={classes.progress_inner} >
                    <input id={classes.step1} name="step" type="radio"
                        ref={this.button1} value="1" onClick={event => { this.loadSteps(event) }} />
                    <input id={classes.step2} name='step' type='radio'
                        ref={this.button2} value="2" onClick={event => { this.loadSteps(event) }} />
                    <input id={classes.step3} name='step' type='radio'
                        ref={this.button3} value="3" onClick={event => { this.loadSteps(event) }} />
                    <div className={classes.progress_inner__step}>
                        <label for={classes.step1}>Base</label>
                    </div>
                    <div className={classes.progress_inner__step}>
                        <label for={classes.step2}>Proteins</label>
                    </div>
                    <div className={classes.progress_inner__step}>
                        <label for={classes.step3}>Toppings</label>
                    </div>
                    <div className={classes.progress_inner__bar}></div>
                    <div className={classes.progress_inner__bar_set}></div>

                </div>
                <div className={classes.Card}>
                    <fieldset>{step}
                        {this.state.goUpDisable ? <a href="#" className={leftArrowDisabledClasses.join(' ')} onClick={(event => this.stepsGoUp(event))}></a> :
                            <a href="#" className={leftArrowClasses.join(' ')} onClick={props.goUp}></a>}
                        {this.state.goDownDisable ? <a href="#" className={rightArrowDisabledClasses.join(' ')} onClick={props.goDown}></a> :
                            <a href="#" className={rightArrowClasses.join(' ')} onClick={(event => this.stepsGoDown(event))}></a>}
                    </fieldset>
                </div>


