import React, { Component, useState, useEffect } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBStepper, MDBStep, MDBBtn, MDBInput } from "mdbreact";
import classes from './Cursor.module.css';
import Auxilliary from '../../../hoc/Auxilliary';
import { motion, useMotionValue, useSpring } from "framer-motion"

function Cursor() {

  const cursorX = useMotionValue(-100);
  const cursorY = useMotionValue(-100);
  const springConfig = { damping: 25, stiffness: 700 };
  const cursorXSpring = useSpring(cursorX, springConfig);
  const cursorYSpring = useSpring(cursorY, springConfig);


  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [clicked, setClicked] = useState(false);
  const [linkHovered, setLinkHovered] = useState(false);
  const [hidden, setHidden] = useState(false);

  useEffect(() => {
      addEventListeners();
      handleLinkHoverEvents();
      return () => removeEventListeners();
  }, []);

  const addEventListeners = () => {
      document.addEventListener("mousemove", onMouseMove);
    //   document.addEventListener("mouseenter", onMouseEnter);
    //   document.addEventListener("mouseleave", onMouseLeave);
      document.addEventListener("mousedown", onMouseDown);
      document.addEventListener("mouseup", onMouseUp);
  };

  const removeEventListeners = () => {
      document.removeEventListener("mousemove", onMouseMove);
    //   document.removeEventListener("mouseenter", onMouseEnter);
    //   document.removeEventListener("mouseleave", onMouseLeave);
      document.removeEventListener("mousedown", onMouseDown);
      document.removeEventListener("mouseup", onMouseUp);
  };

  const onMouseMove = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
  };

  const onMouseDown = () => {
      setClicked(true);
  };

  const onMouseUp = () => {
      setClicked(false);
  };

//   const onMouseLeave = () => {
//       setHidden(true);
//   };

//   const onMouseEnter = () => {
//       setHidden(false);
//   };

  const handleLinkHoverEvents = () => {
      document.querySelectorAll("button").forEach((el) => {
          el.addEventListener("mouseover", () => setLinkHovered(true));
          el.addEventListener("mouseout", () => setLinkHovered(false));
      });
  };

  let cursorClasses = [classes.cursor];

  if(clicked){
    cursorClasses.push(classes.clicked);
  }
//   if(hidden){
//     cursorClasses.push(classes.hidden);
//   }
  if(linkHovered){
    cursorClasses.push(classes.hovered);
  }


  return (
    <Auxilliary>
      <div
          className={cursorClasses.join(' ')}
          style={{ left: `${position.x}px`, top: `${position.y}px` }}
      />
</Auxilliary>
  );
};

export default Cursor;
