            this.input3.current.style.background = 'gray';            
            this.input1.current.style.opacity = 0.5;
            this.input2.current.style.opacity = 0.2;
            this.input3.current.style.opacity = 0.2;
            this.input1.current.checked = 'checked';
            this.input2.current.checked = '';
            this.input3.current.checked = '';

        }
        if (idx === "2") {
            this.setState({index:"2", showCheckoutButton: false});
            this.input1.current.style.background = 'gray';
            this.input2.current.style.background = 'darkslategray';
            this.input3.current.style.background = 'gray';        
            this.input1.current.style.opacity = 0.2;
            this.input2.current.style.opacity = 0.5;
            this.input3.current.style.opacity = 0.2;
            this.input1.current.checked = '';
            this.input2.current.checked = 'checked';
            this.input3.current.checked = '';
        }
        if (idx === "3") {
            this.setState({index:"3", showCheckoutButton: true});
            this.input1.current.style.background = 'gray';
            this.input2.current.style.background = 'gray';
            this.input3.current.style.background = 'darkslategray';        
            this.input1.current.style.opacity = 0.2;
            this.input2.current.style.opacity = 0.2;
            this.input3.current.style.opacity = 0.5;
            this.input1.current.checked = '';
            this.input2.current.checked = '';
            this.input3.current.checked = 'checked';
        }
    }

    loadSteps = (event) => {
        this.toggleSteps(event.target.value);
