import React, { Component } from 'react';
import Layout from './containers/Layout/Layout';
import PokeBuilder from './containers/PokeBuilder/PokeBuilder';
import { Route, Switch, withRouter, Redirect } from 'react-router-dom';
import CheckOut from './containers/CheckOut/CheckOut';
import Orders from './containers/Orders/Orders';
import Auth from './containers/Auth/Auth';
import Logout from './containers/Auth/Logout/Logout';
import { connect } from 'react-redux';
import *  as actions from './store/actions/index';
import AuthInput from './containers/Auth/AuthInput';
import TestUI from './components/UI/TestUI/TestUI';
import Cursor from './components/UI/Cursor/Cursor';
import Auxilliary from './hoc/Auxilliary';

class App extends Component {
  componentDidMount() {
    this.props.onTryAutoSignup();
    window.addEventListener('resize', this.updateDimensions);

  }

  state = { smallScreen: false };

      updateDimensions = () =>{
        let mediaQuery = window.matchMedia("(max-width: 1000px)");
        if (mediaQuery.matches) {
            this.setState({smallScreen:true});
        }
        else{
            this.setState({smallScreen:false});
        }
    }


  render() {

    let cursor = <Cursor />
    if (this.state.smallScreen){
      cursor = <div></div>  
    }

    let routes = (
      <Switch>
        <Route path="/auth" component={Auth} />
        <Route path="/login" component={AuthInput} />
        <Route path="/testui" component={TestUI} />

        <Route path="/" exact component={PokeBuilder} />
        <Redirect to="/" />
      </Switch>);
    if (this.props.isAuthenticated) {
      routes = (
        <Switch>
          <Route path="/logout" component={Logout} />
          <Route path="/checkout" component={CheckOut} />
          <Route path="/orders" component={Orders} />
          <Route path="/auth" component={Auth} />
          <Route path="/login" component={AuthInput} />
          <Route path="/testui" component={TestUI} />
          <Route path="/" exact component={PokeBuilder} />
          <Redirect to="/" />
          {/* <Route path="/" exact render={props => <PokeBuilder/>} /> */}
        </Switch>);
    }

    return (
      <div >
        {cursor}
        <Layout>
          {routes}
        </Layout>
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  };
}


const mapDispatchToProps = dispatch => {
  return {
    onTryAutoSignup: () => dispatch(actions.authCheckState())
  };
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App));
