import 'bootstrap/dist/css/bootstrap.min.css';
import 'mdb-react-ui-kit/dist/css/mdb.min.css'

import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { BrowserRouter } from 'react-router-dom';
import { Provider } from 'react-redux';
import {createStore,applyMiddleware, compose, combineReducers} from 'redux';
import pokeBuilderReducer from './store/reducers/pokeBuilder';
import orderReducer from './store/reducers/order';
import authReducer from './store/reducers/auth';

import thunk from 'redux-thunk';
//redux development tools will only be visible in dev mode 

import TestUI from './components/UI/TestUI/TestUI';

let composeEnhancers = compose;
if (process.env.NODE_ENV ==='development'){
  composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose; 
}

const rootReducer = combineReducers({
  pokeBuilder: pokeBuilderReducer,
  order: orderReducer,
  auth: authReducer
});

const store = createStore(rootReducer,composeEnhancers(applyMiddleware(thunk)));

const app = (
  //connect react app with redux store
  <Provider store = {store}>    
    <BrowserRouter>
      <App />
    </BrowserRouter>
  </Provider>

)


ReactDOM.render(
  app, document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
