import classes from './ContactData.module.css';
import React, { Component } from 'react';
import Button from '../../../components/UI/Button/Button';
import axios from '../../../axios-orders';
import Spinner from '../../../components/UI/Spinner/Spinner';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {
    state = {
        orderForm: {
            name: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Your Name'
                },
                value: ''
            },
            street: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Street'
                },
                value: ''
            },
            
            postcode: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Postcode'
                },
                value: ''
            },
            phone: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Phone Number'
                },
                value: ''
            },
            deliveryMethod: {
                elementType: 'select',
                elementConfig: {
                    options: [
                        { value: 'fastest', displayValue: 'Fastest' },
                        { value: 'cheapest', displayValue: 'Cheapest' },

                    ]
                },
                value: ''
            },
            loading: false
        },

    }

    orderHandler = () => {
        this.setState({ loading: true });
        console.log(this.props.history);
        const order = {
            ingredients: this.props.ingredients,
            price: this.props.price,
            customer: {
                name: 'Test',
                address: '123 test street',
                phone: '0411000000',
            },
            deliveryMethods: 'leave at door'
        };
        axios.post('/orders.json', order)
            .then(response => {
                this.setState({ loading: false });
                this.props.history.push('/');
            })
            .catch(this.setState({ loading: false }));
    }

    inputChangedHandler=(event, inputIdentifier)=>{ //display the typed in content on the form in real time 
        const updatedOrderForm = {
            ...this.state.orderForm
        };
        const updatedFormElement = {
            ...updatedOrderForm[inputIdentifier]
        }; //deep clone
        updatedFormElement.value = event.target.value;
        updatedOrderForm[inputIdentifier]= updatedOrderForm;
        this.setState({orderForm: updatedOrderForm});
    }

    render() {
        const formElementsArray = [];
        for(let key in this.state.orderForm){
            formElementsArray.push({
                id:key,
                config: this.state.orderForm[key]
            })
        }
        let form = (<form>
            {formElementsArray.map(formElement =>(
                <Input 
                key = {formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value = {formElement.config.value}
                changed={(event)=>this.inputChangedHandler(event,formElement.id)}
                />
            ))}
            <Button btnType="Success"
                clicked={this.orderHandler}>
                Order</Button>
        </form>);

        if (this.state.loading) {
            form = <Spinner />;
        }
        return (
            <div>
                <h4>Please Enter your Contact Info</h4>
                {form}
            </div>
        );
    }
}

export default ContactData;
