import axios from '../../axios-orders';
import * as actionTypes from './actionTypes';
//action creators
export const addIngredient = (name) => {
    return {
        type: actionTypes.ADD_INGREDIENT,
        ingredientName: name
    };
};
export const removeIngredient = (name) => {
    return {
        type: actionTypes.REMOVE_INGREDIENT,
        ingredientName: name
    };
};
export const setIngredients = (ingredients) => {
    return {
        type: actionTypes.SET_INGREDIENTS,
        ingredients: ingredients
    };
};

export const fetchIngredientsFailed = (ingredients) => {
    return {
        type: actionTypes.FETCH_INGREDIENTS_FAILED,
        ingredients: ingredients
    };
};
export const initIngredients = () => {
    console.log('here0');
    const request = axios.get('https://poke-builder-87324-default-rtdb.firebaseio.com/ingredients.json');
    return dispatch => {
        const onSuccess = success => {
            console.log('here', success.data);
            dispatch(setIngredients(success.data));
            return success;
        }
        const onError = error => {
            dispatch(fetchIngredientsFailed());
            return error;
        }
        request.then(success => onSuccess, error => {
                
            });
    }
}
