import React from 'react';
import classes from './Poke.module.css';
import PokeIngredients from './PokeIngredients/PokeIngredients';
import background from '../../assets/images/pokeIngredients/emptyBowl4.jpg';
import ingredients from '../UI/TestUI/Ingredients/Ingredients';

const poke = (props) => {
    let transformedIngredients = [];
    let numofIngreds  = 0;
    for( var el in props.ingredients ) {
        if( props.ingredients.hasOwnProperty( el ) ) {
          numofIngreds += parseFloat( props.ingredients[el] );
        }
      } 
    const typeOfIngreds = Object.keys(props.ingredients).length;
    console.log(typeOfIngreds) 
    Object.keys(props.ingredients)
    .forEach((igKey )=> {
            return [...Array(props.ingredients[igKey])].forEach((_, i) => {
                    transformedIngredients.push(<div><PokeIngredients key={igKey + i} type={igKey} indx={props.ingredients[igKey]*i} /></div>);
                });
        });
    // .reduce((arr, el) => {
    //     return arr.concat(el);
    // },[]);


    // if (transformedIngredients.length === 0) {
    //     transformedIngredients = <p>Please add at least 1 ingredient</p>
    // } 
    
    return (
        <div className={classes.Poke} style={{ backgroundImage: `url(${background})` }}>
            <div className={classes.BowlContainer}>{transformedIngredients}</div>
            
            {/* <PokeIngredients type="bread-top" />
            {transformedIngredients}
            <PokeIngredients type="bread-buttom" /> */}
        </div>
    );
};

export default poke;
