import React, { Component } from 'react';
import classes from './PokeIngredients.module.css'
import PropTypes from 'prop-types';
import Ingredients from '../../UI/TestUI/Ingredients/Ingredients';
import Bowl from '../../UI/TestUI/Ingredients/Bowl';

class PokeIngredients extends Component{


    render(){
        let ingredient = null;
        switch (this.props.type) {
            case ('egg'):
                ingredient = <Ingredients imgName = 'boiledEgg.png' type="protein" numOfIngs = {this.props.totalIngs} indx={this.props.indx}/>
                break;
            case ('prawn'):
                ingredient = <Ingredients imgName = 'prawn.png' type="protein" numOfIngs = {this.props.totalIngs} indx={this.props.indx}/>
                break;
            case ('lettuce'):
                ingredient = <Ingredients imgName = 'lettuceBGfree.png' type="topping" numOfIngs = {this.props.totalIngs} indx={this.props.indx}/>
                break;
    
            case ('quinoa'):
                ingredient = <Ingredients imgName = 'quinoaBgfree.png' type="base" numOfIngs = {this.props.totalIngs} indx={this.props.indx}/>
                break;
            case ('whiterice'):
                ingredient = <Ingredients imgName = 'whiteRice.png' type="base" numOfIngs = {this.props.totalIngs} indx={this.props.indx}/>
                break;
            case ('brownrice'):
                ingredient = <Ingredients imgName = 'brownRice.png' type="base" numOfIngs = {this.props.totalIngs} indx={this.props.indx}/>
                break;
            
            default: ingredient = null;
        }
        // ingredient = <div className = {classes.PokeIngredients}>{ingredient}</div>
        return <div className={classes.PokeIngredients}>{ingredient}</div>;
