import React, { Component, useState, render } from 'react';
import classes from './TestUI.module.css';
import Carousel from 'react-bootstrap/Carousel';
import bgimage from '../../../assets/images/burger-logo.png';
import Logo from '../../Logo/Logo';

class TestUI extends Component {

    constructor(props) {
        super(props);
        this.button1 = React.createRef();
        this.button2 = React.createRef();
        this.button3 = React.createRef();
      }
    state = {
        index: 1,
        step: <h1>Step 1</h1>
    };

    handleSelect = (event) => {
        this.setState({ index: event });
    };

    toggleSteps = (idx) =>{
        if (idx === "1") {
            this.setState({ index:1, step: <h1>Step 1</h1> });
            this.button1.current.style.background = 'peru';
        }
        if (idx === "2") {
            this.setState({index:2, step: <h1>Step 2</h1> });
            this.button2.current.style.background = 'peru';
        }
        if (idx === "3") {
            this.setState({index:3, step: <h1>Step 3</h1> });
            this.button3.current.style.background = 'peru';
        }
    }

    loadSteps = (event) => {
        this.toggleSteps(event.target.value);
    }

    stepsGoDown = () =>{
        this.toggleSteps(String(this.state.index % 3+1) );        
    }

    render() {
        return (
            <div>
                {this.state.step}
                <div className={classes.pagination}>
                    <button className={classes.button} value="1" ref={this.button1}
                    onClick={event => { this.loadSteps(event) }}>1</button>
                    <button className={classes.button} value="2" ref={this.button2}
                    onClick={event => { this.loadSteps(event) }}>2</button>
                    <button className={classes.button} value="3"  ref={this.button3}
                     onClick={event => { this.loadSteps(event) }}>3</button>

                    <div className={classes.downArrow}>
                        <div className={classes.bounce}>
                            <img width="20" height="20" alt=""
                            onClick={()=>{this.stepsGoDown()}}
                             src="data:image/svg+xml;base64," /></div>
                    </div>
                </div>
            </div>


        );
    };

}


export default TestUI;
