import React, { Component, useState, useEffect } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBStepper, MDBStep, MDBBtn, MDBInput } from "mdbreact";
import classes from './TestUI.module.css';
import Auxilliary from '../../../hoc/Auxilliary';
import { motion, useMotionValue, useSpring } from "framer-motion"

function TestUI() {

 return(<Auxilliary>

<div className={classes.section}>
    <div className={classes.container}>
      <div className="row fullHeight justify-content-center">
        <div className="col-12 text-center align-self-center py-5">
          <div className="section pb-5 pt-5 pt-sm-2 text-center">
            <h6 className="mb-0 pb-3"><span>Log In </span><span>Sign Up</span></h6>
                  <input className="checkbox" type="checkbox" id="reg-log" name="reg-log"/>
                  <label for="reg-log"></label>
            <div className="card3dWrap mx-auto">
              <div className="card3dWrapper">
                <div className="cardFront">
                  <div className="centerWrap">
                    <div className="section text-center">
                      <h4 className="mb-4 pb-3">Log In</h4>
                      <div className="form-group">
                        <input type="email" name="logemail" className="form-style" placeholder="Your Email" id="logemail" autocomplete="off"/>
                        <i className="input-icon uil uil-at"></i>
                      </div>  
                      <div className="form-group mt-2">
                        <input type="password" name="logpass" className="form-style" placeholder="Your Password" id="logpass" autocomplete="off"/>
                        <i className="input-icon uil uil-lock-alt"></i>
                      </div>
                      <a href="#" className="btn mt-4">submit</a>
                                    <p className="mb-0 mt-4 text-center"><a href="#0" className="link">Forgot your password?</a></p>
                        </div>
                      </div>
                    </div>
                <div className="cardBack">
                  <div className="centerWrap">
                    <div className="section text-center">
                      <h4 className="mb-4 pb-3">Sign Up</h4>
                      <div className="form-group">
                        <input type="text" name="logname" className="form-style" placeholder="Your Full Name" id="logname" autocomplete="off"/>
                        <i className="input-icon uil uil-user"></i>
                      </div>  
                      <div className="form-group mt-2">
                        <input type="email" name="logemail" className="form-style" placeholder="Your Email" id="logemail" autocomplete="off"/>
                        <i className="input-icon uil uil-at"></i>
                      </div>  
                      <div className="form-group mt-2">
                        <input type="password" name="logpass" className="form-style" placeholder="Your Password" id="logpass" autocomplete="off"/>
                        <i className="input-icon uil uil-lock-alt"></i>
                      </div>
                      <a href="#" className="btn mt-4">submit</a>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
      </div>
  </div>
 </Auxilliary>);
};
