import React, { Component } from 'react';
import Input from '../../components/UI/Input/Input';
import Button from '../../components/UI/Button/Button';
import classes from './Auth.module.css';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
import Spinner from '../../components/UI/Spinner/Spinner';
import { Redirect } from 'react-router-dom';
import { checkValidity } from '../../shared/utility';
import Alert from 'react-bootstrap/Alert';
import 'bootstrap/dist/css/bootstrap.min.css';

class Auth extends Component {

    state = {
        controls: {
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'Email'
                },
                value: '',
                validation: {
                    required: true,
                    isEmail: true
                },
                validity: false,
                touched: false
            },
            password: {
                elementType: 'input',
                elementConfig: {
                    type: 'password',
                    placeholder: 'Password'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 6
                },
                validity: false,
                touched: false
            },
        },
        isSignUp: true
    }

    inputChangedHandler = (event, controlName) => {
        const updatedControls = {
            ...this.state.controls,
            [controlName]: {
                ...this.state.controls[controlName],
                value: event.target.value,
                validity: checkValidity(event.target.value, this.state.controls[controlName].validation),
                touched: true
            }
        };
        this.setState({ controls: updatedControls });
    }

    submitHandler = (event) => {
        event.preventDefault();
        this.props.onAuth(this.state.controls.email.value, this.state.controls.password.value, this.state.isSignUp);
    }

    switchAuthModeHandler = () => {
        this.setState(prevState => {
            return {
                isSignUp: !prevState.isSignUp
            }
        });
    }

    errorMessage = () => { //rearrang the error message from firebase
        let errorMessage = null;
        console.log(this.props.error)
        if (this.props.error) {
            const lower = String(this.props.error.message).toLowerCase().replace('_', ' ');
            const msgText = lower.charAt(0).toUpperCase() + lower.slice(1);
            errorMessage = (
                <Alert key={msgText} variant="danger">{msgText}</Alert>
            );
        }
        return errorMessage
    }


    componentDidMount() {
        if (!this.props.buildingPoke && this.props.authRedirectPath !== '/') {
            this.props.onSetAuthRedirectPath();
        }
    }

    render() {
        const formElementsArray = [];
        for (let key in this.state.controls) {
            formElementsArray.push({
                id: key,
                config: this.state.controls[key]
            });
        }

        let form = formElementsArray.map(formElement => (
            <Input
                key={formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                valid={formElement.config.validity}
                touched={formElement.config.touched}
                changed={(event) => this.inputChangedHandler(event, formElement.id)} />
        ));

        if (this.props.loading) {
            form = <Spinner />
        }


        let authRedirect = null;
        if (this.props.isAuthenticated) {
            authRedirect = <Redirect to={this.props.authRedirectPath} />
        }

        const errorMessage = this.errorMessage();
        return (
            <div className={classes.loginBG}>
                <div className={classes.section}>
                    <h1 className={classes.heading}>
                        <span>Log In</span>
                        <span>Sign Up</span>
                    </h1>
                    <input className={classes.checkbox} type="checkbox" id="reg-log" name="reg-log"  onClick = {this.switchAuthModeHandler}/>
                    <label for="reg-log"></label>
                    <div className={classes.card3dWrap}>
                        <div className={classes.card3dWrapper}>

                            <form onSubmit={this.submitHandler} className={classes.cardFront}>
                                <div className={classes.centerWrap}>
                                    <h2 >Sign Up</h2>
                                    {form}
                                    {errorMessage}
                                    <button className={classes.btn}>submit</button>
                                </div>
                            </form>

                            <form onSubmit={this.submitHandler} className={classes.cardBack}>
                                <div >
                                    <div className={classes.centerWrap}>
                                        <h2 >Log In</h2>
                                        {form}
                                        {errorMessage}
                                        <button className={classes.btn}>submit</button>                                    
                                        <p className="mb-0 mt-4 text-center"><a href="#0" className={classes.link}>Forgot your password?</a></p>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

        );
    }
}

const mapStateToProps = state => {
    return {
        loading: state.auth.loading,
        signInError: state.auth.signInError,
        signUpError: state.auth.signUpError,
        isAuthenticated: state.auth.token !== null,
        buildingPoke: state.pokeBuilder.building,
        authRedirectPath: state.auth.authRedirectPath
    }
}



const mapDispatchToProps = dispatch => {
    return {
        onAuth: (email, password, isSignUp) => dispatch(actions.auth(email, password, isSignUp)),
        onSetAuthRedirectPath: () => dispatch(actions.setAuthRedirectPath('/'))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Auth);





