import React, { Component } from 'react';
import classes from './Carousel.module.css';
import BuildControls from '../../components/Poke/BuildControls/BuildControls';

class Carousel extends Component {

    constructor(props) {
        console.log('super');
        super(props);
        console.log('after super');

        this.button1 = React.createRef();
        this.button2 = React.createRef();
        this.button3 = React.createRef();
      }

    state = {
        index: 1,
        step: <h1>Step 1</h1>
    };



    toggleSteps = (idx) =>{
        console.log('toggle');
        if (idx === "1") {
            console.log('idx=1');

            this.setState({ index:1, 
                step: <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset = {this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.ordered}
                disable={this.props.disable}
                ings = {this.props.ings} 
                category = "base"/>});
            this.button1.current.style.background = 'darkslategray';
            this.button2.current.style.background = 'gray';
            this.button3.current.style.background = 'gray';            
            this.button1.current.style.opacity = 0.5;
            this.button2.current.style.opacity = 0.2;
            this.button3.current.style.opacity = 0.2;
        }
        if (idx === "2") {
            this.setState({index:2, 
                step: <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset = {this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.ordered}
                disable={this.props.disable}
                ings = {this.props.ings} 
                category = "protein"/>});
            this.button1.current.style.background = 'gray';
            this.button2.current.style.background = 'darkslategray';
            this.button3.current.style.background = 'gray';        
            this.button1.current.style.opacity = 0.2;
            this.button2.current.style.opacity = 0.5;
            this.button3.current.style.opacity = 0.2;
        }
        if (idx === "3") {
            this.setState({index:3, 
                step: <BuildControls
                isAuth={this.props.isAuth}
                ingredientAdded={this.props.ingredientAdded}
                ingredientRemoved={this.props.ingredientRemoved}
                ingredientsReset = {this.props.ingredientsReset}
                totalPrice={this.props.totalPrice}
                purchasable={this.props.purchasable}
                ordered={this.ordered}
                disable={this.props.disable}
                ings = {this.props.ings} 
                category = "toppings"/>});
            this.button1.current.style.background = 'gray';
            this.button2.current.style.background = 'gray';
            this.button3.current.style.background = 'darkslategray';        
            this.button1.current.style.opacity = 0.2;
            this.button2.current.style.opacity = 0.2;
            this.button3.current.style.opacity = 0.5;
        }
    }

    loadSteps = (event) => {
        console.log('here');
        this.toggleSteps(event.target.value);
    }

    stepsGoDown = () =>{
        this.toggleSteps(String(this.state.index % 3+1) );        
    }

    render() {
        return (
            <div>
                {this.state.step}
                <div className={classes.pagination}>
                    <button className={classes.button} value="1" ref={this.button1}
                    onClick={event => { this.loadSteps(event) }}>1</button>
                    <button className={classes.button} value="2" ref={this.button2}
                    onClick={event => { this.loadSteps(event) }}>2</button>
                    <button className={classes.button} value="3"  ref={this.button3}
                     onClick={event => { this.loadSteps(event) }}>3</button>

                    <div className={classes.downArrow}>
                        <div className={classes.bounce}>
                            <img width="20" height="20" alt=""
                            onClick={()=>{this.stepsGoDown()}}
                             src="data:image/svg+xml;base64," /></div>
                    </div>
                </div>
            </div>


        );
    };

}


export default Carousel;
